import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SERVER_URL = 'http://localhost:3001'; // Replace with your server's URL

const App = () => {

  const [accessToken, setAccessToken] = useState(null)
  const handleLogin = () => {
    // Redirect the user to the server's login endpoint
    window.location.href = `${SERVER_URL}/login`;
  };

  useEffect(() => {
    console.log(accessToken)
    // Handle the callback after successful login
    const handleCallback = () => {
      const params = new URLSearchParams(window.location.search);
      const code = params.get('code');

      // If there is a code parameter in the URL, send it to the server to get the access token
      if (code) {
        axios.get(`${SERVER_URL}/callback?code=${code}`)
          .then(response => {
            console.log('User Data:', response.data);
            localStorage.setItem('accessToken', response.data.access_token)
            setAccessToken(response.data.access_token)
            // Optionally, you can save the access token in state or local storage for future use
          })
          .catch(error => console.error('Error:', error));
      }
    };

    // Call handleCallback when the component mounts to handle the callback
    handleCallback();
  }, []);

  return (
    <div>
      <h1>Spotify Authentication</h1>
      {accessToken ? (
        <div>
          <p>Access Token: {accessToken}</p>
          {/* Use the access token for other API requests or other actions */}
        </div>
      ) : (
        <button onClick={handleLogin}>Login with Spotify</button>
      )}
    </div>
  );
};

export default App;
