import React, { useEffect, useState } from 'react'
import axios from 'axios'

const App = () => {
  const [accessToken, setAccessToken] = useState(null)
  const [tracks, setTracks] = useState(null)

  useEffect(() => {
    const storedAccessToken = localStorage.getItem('accessToken')
    if (storedAccessToken) { setAccessToken(storedAccessToken) }
    const params = new URLSearchParams(window.location.search)
    const access_token = params.get('access_token')
    if (access_token) {
      localStorage.setItem('accessToken', access_token)
      setAccessToken(access_token)
      window.location.href = '/'
    }
  }, [])

  const handleProfile = () => {
    axios.get('/track', {
      params: {
        access_token: accessToken
      }
    })
      .then(response => {
        setTracks(response.data)
        console.log(response.data)
      })
      .catch(error => {
        console.error(error)
      })
  }

  const handleLogout = () => {
    localStorage.removeItem('accessToken')
    setAccessToken(null)
  }

  const handleChoice = (id) => {
    if (id === tracks.lyricsIndex)
      console.log('yesss')
  }

  return (
    <div>
      <h1>Spotify Authentication</h1>
      {accessToken ? (
        <div>
          <p>Access Token: {accessToken}</p>
          <button onClick={handleProfile}>Show user</button>
          <button onClick={handleLogout}>Logout</button>
          {tracks && (
            <div>
              <ul>
                {tracks.idTitles.map(idTitle => <button onClick={handleChoice(idTitle.id)}>{idTitle.title}</button>)}
              </ul>
              {tracks.lyrics.map(line => <>{line}<br /></>)}
            </div>
          )}
        </div>
      ) : (
        <button onClick={() => window.location.href = '/login'}>Login with Spotify</button>
      )}
    </div>
  )
}

export default App
