import React, { useEffect, useState } from 'react'

const App = () => {
  const clientId = '40f3d875372f4fbd9850e002c95a8078'
  const [profile, setProfile] = useState(null)

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const code = params.get('code');
    const accessToken = localStorage.getItem('accessToken'); // Get the access token from localStorage

    const authenticate = async () => {
      if (!code && !accessToken) {
        redirectToAuthCodeFlow(clientId);
      } else {
        const tokenToUse = code || accessToken; // Use the code from the URL or the stored access token
        const userProfile = await fetchProfile(tokenToUse);
        setProfile(userProfile);
        localStorage.setItem('accessToken', tokenToUse); // Store the access token in localStorage
      }
    }

    authenticate()

    async function redirectToAuthCodeFlow(clientId) {
      const verifier = generateCodeVerifier(128)
      const challenge = await generateCodeChallenge(verifier)

      localStorage.setItem('verifier', verifier)

      const params = new URLSearchParams()
      params.append('client_id', clientId)
      params.append('response_type', 'code')
      params.append('redirect_uri', 'http://localhost:3001')
      params.append('scope', 'user-read-private user-read-email')
      params.append('code_challenge_method', 'S256')
      params.append('code_challenge', challenge)

      document.location = `https://accounts.spotify.com/authorize?${params.toString()}`
    }
  }, [])

  useEffect(() => {
    if (profile) {
      populateUI(profile)
    }
  }, [profile])

  const generateCodeVerifier = (length) => {
    let text = ''
    let possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789'

    for (let i = 0; i < length; i++) {
      text += possible.charAt(Math.floor(Math.random() * possible.length))
    }
    return text
  }

  const generateCodeChallenge = async (codeVerifier) => {
    const data = new TextEncoder().encode(codeVerifier)
    const digest = await window.crypto.subtle.digest('SHA-256', data)
    return btoa(String.fromCharCode.apply(null, [...new Uint8Array(digest)]))
      .replace(/\+/g, '-')
      .replace(/\//g, '_')
      .replace(/=+$/, '')
  }

  const getAccessToken = async (clientId, code) => {
    const verifier = localStorage.getItem('verifier')

    const params = new URLSearchParams()
    params.append('client_id', clientId)
    params.append('grant_type', 'authorization_code')
    params.append('code', code)
    params.append('redirect_uri', 'http://localhost:3001')
    params.append('code_verifier', verifier)

    const result = await fetch('https://accounts.spotify.com/api/token', {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: params
    })

    const { access_token } = await result.json()
    return access_token
  }

  const fetchProfile = async (token) => {
    const result = await fetch('https://api.spotify.com/v1/me', {
      method: 'GET', headers: { Authorization: `Bearer ${token}` }
    })

    return await result.json()
  }

  const populateUI = (profile) => {
    document.getElementById('displayName').innerText = profile.display_name
    if (profile.images[0]) {
      const profileImage = new Image(200, 200)
      profileImage.src = profile.images[0].url
      document.getElementById('avatar').appendChild(profileImage)
      document.getElementById('imgUrl').innerText = profile.images[0].url
    }
    document.getElementById('id').innerText = profile.id
    document.getElementById('email').innerText = profile.email
    document.getElementById('uri').innerText = profile.uri
    document.getElementById('uri').setAttribute('href', profile.external_urls.spotify)
    document.getElementById('url').innerText = profile.href
    document.getElementById('url').setAttribute('href', profile.href)
  }

  return (
    <div>
      <h1>Display your Spotify Profile Data</h1>

      <section id="profile">
        <h2>Logged in as <span id="displayName"></span></h2>
        <img alt="avatar" id="avatar" width="200" src="https://i.scdn.co/image/ab67757000003b82b6c88b779133be935251d08a" />
        <ul>
          <li>User ID: <span id="id"></span></li>
          <li>Email: <span id="email"></span></li>
          <li>Spotify URI: <a id="uri" href="#"></a></li>
          <li>Link: <a id="url" href="#"></a></li>
          <li>Profile Image: <span id="imgUrl"></span></li>
        </ul>
      </section>
    </div>
  )
}

export default App
