import React, { useEffect, useState } from 'react';
import axios from 'axios'

const App = () => {
  const [accessToken, setAccessToken] = useState(null);

  useEffect(() => {
    const handleCallback = () => {
      const params = new URLSearchParams(window.location.search);
      const access_token = params.get('access_token');

      console.log(access_token)

      if (access_token) {
        localStorage.setItem('accessToken', access_token);
        setAccessToken(access_token);
        window.location.href = '/';
      }
    };

    // Check if there is a token in localStorage when the component mounts
    const storedAccessToken = localStorage.getItem('accessToken');
    if (storedAccessToken) {
      setAccessToken(storedAccessToken);
    }

    handleCallback();
  }, []);

  const handleLogin = () => {
    window.location.href = '/login'; // Replace with your server's login URL
  };

  const handleProfile = () => {
    console.log(accessToken.token_type)
    console.log(accessToken.access_token)
    axios.get('https://api.spotify.com/v1/me', {
        headers: {
          Authorization: `${accessToken.token_type} ${accessToken.access_token}`
        }
      })
        .then(response => {
          console.log(response.data)
        })
        .catch(error => {
          console.log(error)
        })
  }

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    setAccessToken(null);
  };

  return (
    <div>
      <h1>Spotify Authentication</h1>
      {accessToken ? (
        <div>
          <p>Access Token: {accessToken}</p>
          <button onClick={handleProfile}>Show user</button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      ) : (
        <button onClick={handleLogin}>Login with Spotify</button>
      )}
    </div>
  );
};

export default App;
