import React, { useEffect } from 'react';
import axios from 'axios';

const SERVER_URL = 'http://localhost:3001'; // Replace with your server's URL
const BASE_URL = 'http://localhost:3000'; // Replace with your base URL

const App = () => {
  useEffect(() => {
    // Handle the callback after successful login
    const handleCallback = () => {
      const params = new URLSearchParams(window.location.search);
      const code = params.get('code');

      // If there is a code parameter in the URL, send it to the server to get the access token
      if (code) {
        axios.get(`${SERVER_URL}/callback?code=${code}`)
          .then(response => {
            console.log('User Data:', response.data);
            // Save the access token in localStorage for future use
            localStorage.setItem('accessToken', response.data.access_token);
            // Redirect to the base URL after saving the token
            window.location.href = BASE_URL;
          })
          .catch(error => console.error('Error:', error));
      }
    };

    // Call handleCallback when the component mounts to handle the callback
    handleCallback();
  }, []);

  return (
    <div>
      <h1>Spotify Authentication</h1>
      {/* You can add any content you want here */}
    </div>
  );
};

export default App;
